values (#{id,jdbcType=INTEGER}, #{serverId,jdbcType=INTEGER}, #{pid,jdbcType=INTEGER}, 
  #{name,jdbcType=VARCHAR}, #{port,jdbcType=INTEGER}, #{detail,jdbcType=VARCHAR}, 
  #{rootPath,jdbcType=VARCHAR}, #{startupSh,jdbcType=VARCHAR}, #{shutdownSh,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{deleted,jdbcType=BIT}, #{version,jdbcType=INTEGER}
  )
